// Funktsioon POST päringu saatmiseks
export function post(endpoint, data) {
    return fetch(endpoint, {
        method: 'POST', // Määrab päringu meetodi POST
        credentials: 'include', // Lisab küpsised päringule, kui need on saadaval
        body: JSON.stringify(data || {}), // Muudab andmed JSON-formaadiks
        headers: {
            'Content-Type': 'application/json' // Määrab sisu tüübi JSON
        }
    }).then((r) => r.json()) // Tagastab vastuse JSON-formaadis
}
